mapper.select:
select BT.x_pos , BT.y_pos , BT.now_station_no , BT.next_station_no , BT.status
from BUS_TB AS BT
where vehicle_no = #{vehicle_no}

select BST.id as now_station_id , BST.latitude as now_ypos , BST.longitude as now_xpos , BST.station_name as now_station_name ,BST2.id as next_station_id , BST2.latitude as next_ypos , BST2.longitude as next_xpos , BST2.station_name as next_station_name ,BST3.id as step2_station_id , BST3.latitude as step2_ypos , BST3.longitude as step2_xpos , BST3.station_name as step2_station_name
from BUS_TB as BT join BUS_ROUTE_TB AS BRT on BT.id = BRT.route_num and BT.now_station_no = BRT.bus_station_id and BT.next_station_no = BRT.next_station_id join BUS_ROUTE_TB AS BRT2 on BRT.next_course_id = BRT2.id join BUS_STATION_TB as BST on BST.id = BRT.bus_station_id join BUS_STATION_TB as BST2 on BST2.id = BRT.next_station_id join BUS_STATION_TB as BST3 on BST3.id = BRT2.next_station_id
where vehicle_no = #{vehicle_no} ;

select longitude,latitude
from BUS_STATION_TB AS BST
         join BUS_ROUTE_TB AS BRT
              ON BST.id = BRT.bus_station_id
where BRT.bus_station_id = #{now_station_no}
  and BRT.next_station_id = #{next_station_id}

mapper.update:
UPDATE BUS_TB
SET now_station_no = #{next_station_no}
  ,next_station_no = #{step2_station_no}
  ,status = #{status}
where vehicle_no = #{vehicle_no};

UPDATE BUS_TB
SET status = #{status}
where vehicle_no = #{vehicle_no};
