mapper.update:
UPDATE USER_TB AS UT
SET x_pos = #{x_pos} , y_pos = #{y_pos}
WHERE UT.id = (select owner_id from UWB_MODULE_TB where uuid=#{uuid})

mapper.select:
select status
from SERVICE_TB
where service_id = #{service_id}

mapper.update:
UPDATE SERVICE_TB
SET status = #{status}
where service_id = #{service_id}
